int binarySearch(int arr[], int l, int r, int x)
      {
          if (r >= l) {
      int mid = l + (r - l) / 2;
       
      // Si el elemento actual esta presente en el medio
      if (arr[mid] == x)
          return mid;
      // retornar el medio
       
      //si el elemento es más pequeño que el medio
      // entonces debe de estar presente en el subarray izquierdo
      if (arr[mid] > x)
          return binarySearch(arr, l, mid - 1, x);
             // si no esta el elemento solo puede estar
      //en el subarray derecho
      return binarySearch(arr, mid + 1, r, x);
          }
       
          //Si el elemento no se encuentra en el arreglo
          // retornamos -1
          return -1;
      }

int search(int arr[], int N, int x)
      {
          int i;
          for (i = 0; i < N; i++)
      if (arr[i] == x)
          return i;
          return -1;
      }
